import React, { Component } from 'react'
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import * as firebase from 'firebase';
import {withGetScreen} from 'react-getscreen'


let temp = [];

export class Mobile extends Component {


  constructor(props){
    super(props);
    this.state={
      questions : [],
 
    }
  }
 
  componentDidMount(){
  
    const rootRef = firebase.database().ref();
    const speedRef = rootRef.child('quiz1');
   
    speedRef.on('value', snap=>{
      let questions  = snap.val();
      let newState = [];
      for(let x in questions){
        newState.push({
          id : x,
          op1 : questions[x].op1,
          op2 : questions[x].op2,
          op3 : questions[x].op3,
          op4 : questions[x].op4,
          q: questions[x].question

        })
      }
     
     this.setState({
       questions : newState
     }) 
     
    });

  }

  setGender(event) {
    console.log(event.target.value);
  }

  render() {
    
    return (
      <div className="App" style={{backgroundColor:"#F06292", height:"100%"}}>
        {this.state.questions.map(q=>{
          return(
            <div className="container-fluid" style={{backgroundColor:"#F06292", height:"100%"}}>
            <div className="row">
             

              <div className="col-12">
              <div style={{boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)", margin:"20px", padding:"30px", backgroundColor:"white"}}>
                <div className="card-body">
                  <h1 className="card-title">{q.q}</h1>
                  <div onChange={this.setGender.bind(this)}>
                  
                      <div className="row" style={{backgroundColor:"#F5F5F5", borderRadius:"20px", paddingBottom:"14px", margin:"5px"}}>
                        <div className="col-1" >
                        <input type="radio" value={q.op1} className="form-radio" name="gender"/>
                        </div>
                        <div className="col-9" style={{marginTop:"10px", justifyContent : "start"}}>
                        {q.op1}
                        </div>
                      </div>
                      
                      <div className="row"  style={{backgroundColor:"#F5F5F5", borderRadius:"20px", paddingBottom:"14px", margin:"5px"}}>
                        <div className="col-1">
                        <input type="radio" value={q.op2} className="form-radio" name="gender"/>
                        </div>
                        <div className="col-9" style={{marginTop:"10px", justifyContent : "start"}}>
                        {q.op2}
                        </div>
                      </div>


                      <div className="row"  style={{backgroundColor:"#F5F5F5", borderRadius:"20px", paddingBottom:"14px", margin:"5px"}}>
                        <div className="col-1">
                        <input type="radio" value={q.op3} className="form-radio" name="gender"/>
                        </div>
                        <div className="col-9" style={{marginTop:"10px"}}>
                        {q.op3}
                        </div>
                      </div>

                      <div className="row"  style={{backgroundColor:"#F5F5F5", borderRadius:"20px", paddingBottom:"14px", margin:"5px"}}>
                        <div className="col-1">
                        <input type="radio" value={q.op4} className="form-radio" name="gender"/>
                        </div>
                        <div className="col-9" style={{marginTop:"10px"}}>
                        {q.op4}
                        </div>
                      </div>

                  
                   
                  </div>  
                </div>
                
            </div>

              </div>

            

            </div>
            
            
            </div>
          );
        })}
      </div>
    )
  }
}

export default Mobile
