                            <div className="col-1">
                              <input
                                type="radio"
                                id="oa4"
                                value={q.op4}
                                className="form-radio"
                                name="gender"
                              />
                            </div>
                            <div
                              className="col-9"
                              style={{ marginTop: "10px" }}
                            >
                              {q.op4}
                            </div>
                          </div>

                          <div
                            id="btn"
                            className="row"
                            style={{ marginTop: "40px" }}
