import React, { Component } from 'react'
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import * as firebase from 'firebase';
import {withGetScreen} from 'react-getscreen'
import next from './images/next.png';
import back from './images/back.png';


let temp = [];
let currentIndex = 0;

export class App extends Component {


  constructor(props){
    super(props);
    this.state={
      questions : [],
      currentIndex : 0

    }
  }

  componentDidMount(){

    const rootRef = firebase.database().ref();
    const speedRef = rootRef.child('quiz1');

    speedRef.on('value', snap=>{
      let questions  = snap.val();
      let newState = [];
      for(let x in questions){
        newState.push({
          id : x,
          op1 : questions[x].op1,
          op2 : questions[x].op2,
          op3 : questions[x].op3,
          op4 : questions[x].op4,
          q: questions[x].question

        })
      }

     this.setState({
       questions : newState
     }) 

    });

  }

  setAnswer(event) {
    console.log(event.target.value);
  }


   nextQuestion(){
    this.setState({
      currentIndex : currentIndex + 1
    })
  }

   previousQuestion(){
    console.log("back");
    this.setState({
      currentIndex : currentIndex - 1
    })
  }

  render() {

    return (
      <div className="App" style={{backgroundColor:"#F06292", height:"100%"}}>
        {this.state.questions.slice(currentIndex,1).map(q=>{
          return(
            <div className="container-fluid" style={{backgroundColor:"#F06292", height:"100%"}}>
            <div className="row">
              <div className="col-3 ">
                  <div onClick={function previous(){console.log(currentIndex = currentIndex - 1)}} style={{borderRadius:"50%", backgroundColor:"#F5F5F5", height:"100px", width:"100px", marginTop:"180px"}}>
                  <img src={back} height="20px"  width="20px" style={{marginTop:"40px"}}/>
                  </div>
              </div>

              <div className="col-6">
              <div style={{boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)", margin:"20px", padding:"30px", backgroundColor:"white"}}>
                <div className="card-body">
                  <h1 className="card-title">{q.q}</h1>
                  <div onChange={this.setAnswer.bind(this)}>
                      <div className="row" style={{backgroundColor:"#F5F5F5", borderRadius:"20px", paddingBottom:"14px", margin:"5px"}}>
                        <div className="col-1" >
                        <input type="radio" value={q.op1} className="form-radio" name="gender"/>
                        </div>
                        <div className="col-9" style={{marginTop:"10px", justifyContent : "start"}}>
                        {q.op1}
                        </div>
                      </div>

                      <div className="row"  style={{backgroundColor:"#F5F5F5", borderRadius:"20px", paddingBottom:"14px", margin:"5px"}}>
                        <div className="col-1">
                        <input type="radio" value={q.op2} className="form-radio" name="gender"/>
                        </div>
                        <div className="col-9" style={{marginTop:"10px", justifyContent : "start"}}>
                        {q.op2}
                        </div>
                      </div>


                      <div className="row"  style={{backgroundColor:"#F5F5F5", borderRadius:"20px", paddingBottom:"14px", margin:"5px"}}>
                        <div className="col-1">
                        <input type="radio" value={q.op3} className="form-radio" name="gender"/>
                        </div>
                        <div className="col-9" style={{marginTop:"10px"}}>
                        {q.op3}
                        </div>
                      </div>

                      <div className="row"  style={{backgroundColor:"#F5F5F5", borderRadius:"20px", paddingBottom:"14px", margin:"5px"}}>
                        <div className="col-1">
                        <input type="radio" value={q.op4} className="form-radio" name="gender"/>
                        </div>
                        <div className="col-9" style={{marginTop:"10px"}}>
                        {q.op4}
                        </div>
                      </div>


                      <div className="row" style={{marginTop:"40px"}}>
                        <div  className="col-12" style={{borderRadius:"20px", backgroundColor:"#26A69A", margin:"1px"}}>
                          <h4 style={{color:"white", marginTop:"6px"}}>Submit</h4>
                        </div>


                      </div>


                  </div>  
                </div>

            </div>

              </div>

              <div className="col-3">
              <div onClick={function next(){console.log(currentIndex = currentIndex + 1);
              _nextQuestion(); }} className="float-right" style={{borderRadius:"50%", backgroundColor:"#F5F5F5", height:"100px", width:"100px", marginTop:"180px"}}>
                  <img src={next} height="20px"  width="20px" style={{marginTop:"40px"}}/>
                  </div>
              </div>

            </div>


            </div>
          );
        })}
      </div>
    )
  }
}

export default App
