                            </div>

                            <div
                              onClick={()=>this.submitAnswerApt(q.id)}
                              className="col-3"
                              style={{
                                borderRadius: "20px",
                                backgroundColor: "#26A69A",
                                margin: "1px"
                              }}
                            >
                              <h4 style={{ color: "white", marginTop: "6px" }}>
                                Submit and next
                              </h4>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="col-3">

                  </div>
                </div>
              </div>
            );
          })}

</div>

      </div>
    )

     :



    (
      <div className="container-fluid" style={{ padding: "80px" }}>
        <div className="row">
          <label for="fname">First Name</label>
          <input
            type="text"
            id="fname"
            name="firstname"
            placeholder="Your name.."
          ></input>

          <label for="lname">Last Name</label>
          <input
            type="text"
            id="lname"
            name="lastname"
            placeholder="Your last name.."
          ></input>

          <label for="sysid">System ID</label>
          <input
            type="text"
            id="sysid"
            name="systemud"
            placeholder="Your system id.."
          ></input>

          <label for="lang">Programming Language</label>
          <select id="lang" name="lang">
            <option value="C">C</option>
            <option value="Python">Python</option>
          </select>


          <button style={{marginTop:"20px"}} type="submit" value="Submit" onClick={this.updateDetails.bind(this)}>SUBMIT</button>
        </div>
      </div>
    );
  }
}

export default App;
