import React, { Component } from 'react'
import './App.css'
import * as firebase from 'firebase';

let temp = [];

export class App extends Component {


  constructor(props){
    super(props);
    this.state={
      questions : [],

    }
  }



  componentDidMount(){

    const rootRef = firebase.database().ref();
    const speedRef = rootRef.child('quiz1');

    speedRef.on('value', snap=>{
      let words  = snap.val();
      let newState = [];
      for(let x in words){
        newState.push({
          id : x,
          op1 : words[x].op1,
          op2 : words[x].op2
        })
      }

     this.setState({
       words : newState
     }) 

    });

  }

  render() {

    return (
      <div className="App">
        {this.state.questions.map(q=>{
          console.log(q);
          return(
            <div>
            <h1>rhis is {q[0].question}</h1>
            </div>
          );
        })}
      </div>
    )
  }
}

export default App
