import React, { Component } from 'react'
import './App.css'
import * as firebase from 'firebase';

let temp = [];

export class App extends Component {


  constructor(props){
    super(props);
    this.state={
      questions : [],

    }
  }



  componentDidMount(){

    const rootRef = firebase.database().ref();
    const speedRef = rootRef.child('quiz1');

    speedRef.on('value', snap=>{
     // console.log(snap.val())
      //temp.push(snap.val());
      snap.forEach(function(val){
      //  console.log(val.val().op1);
        temp.push({
          question : val.val().question,

        });
      })


    });



    // this.setState({
    //   questions : temp,

    // });
    // console.log(questions) 

    this.setState({
      questions : [...temp]
    });


  }

  render() {

    return (
      <div className="App">
        {temp.map((q)=>{
          <h1>{q.toString()}</h1>
        })}
      </div>
    )
  }
}

export default App
