  updateDetails() {
    var e = document.getElementById("lang");
    var strUser = e.options[e.selectedIndex].value;
    var fname = document.getElementById("fname").value;
    var lname = document.getElementById("lname").value;
    var sysid = document.getElementById("sysid").value;
    var that = this;

    if (fname != "" && lname != "" && sysid != "" && sysid.length == 10) {
      var user = firebase.auth().currentUser;

      if (user) {
        //console.log(user.uid);
        //that.signin();
        that.setState({
          userLoggedIn: true
        });
        that.setState({
          signedIn: "CONTINUE"
        });
        firebase.database().ref().child("leaderboard1").child(user.uid).set({
          points: 0,
          name: firebase.auth().currentUser.displayName,
          dp: firebase.auth().currentUser.photoURL,
          firstname: fname,
          lastname: lname,
          language: strUser,
          sysid: sysid,
          APTITUDE_POINTS: 0,
          PROGRAMMING_POINTS: 0,
          email: user.email
        }).then(function (val) {
          that.setState({
            det: true,
            plang: strUser
          });
          document.getElementById("signbutton").style.display = "none";
          document.getElementById("profiletag").style.visibility = "visible";
          document.getElementById("username").innerHTML = fname + " " + lname + "<br/>" + "System id : " + sysid + "<br/>" + "Programming language: " + strUser + "<br/>" + "Email: " + user.email;
          document.getElementById("dp").src = user.photoURL;
          const rootRef = firebase.database().ref();
          const speedRef = rootRef.child("quiz1/" + that.state.plang);
          speedRef.on("value", snap => {
            let questions = snap.val();
            let newState = [];
            let randomList = [];

            for (let x in questions) {
              newState.push({
