import React, { Component } from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import * as firebase from "firebase";
import { withGetScreen } from "react-getscreen";
import next from "./images/next.png";
import back from "./images/back.png";
import { Helmet } from "react-helmet";
import logo from './images/A7A_CIRCLE.jpg';
import { toASCII } from "punycode";

import facebook from './images/facebook.png';
import discord from './images/discord.png';
import reddit from './images/reddit.png';
import instagram from './images/instagram.png';
import youtube from './images/youtube.png';
import domain from './images/domain.png';

import sharda from './images/sharda.jpeg';
import shardasmall from './images/shardasmall.jpg';
import A7ABIG from './images/A7A_CIRCLE.jpg';
import mindfizz from './images/mindfizz.jpeg';
import turing from './images/turing.jpeg';

import Countdown from 'react-countdown-now';



let length = 0;
let usersLength = 0;
let temp = [];
let selected;
let selectedOpt;
let selectedApt;
let selectedAptOpt;
let clickedOnce = false;
let UID;
let SC_PROGRAMMING = 0;
let C_PROGRAMMING = 0;
let W_PROGRAMMING = 0;

let SC_APTITUDE = 0;
let C_APTITUDE = 0;
let W_APTITUDE = 0;
let currentCodingIndex = 1;
let currentAptIndex = 1;



export class App extends Component {
  constructor(props) {
    super(props);

    let Canvas = <canvas onContextMenu={(e)=>  {e.preventDefault(); return false;}} height={500} width={500} ref="canvas"/>;

    this.state = {
      time: {}, seconds: 2400 ,

      totalScore: 0,
      currentScore: 0,
      correct: 0,
      wrong: 0,
      questions: [],
      Aptquestions: [],
      leaderboard: [],
      currentIndex: 0,
      currentIndexApt:0,
      det: false,
      userLoggedIn:false,
      plang:"",
      currentQuestion:1,
      signedIn:"LOGIN",
      alreadyAttemptedOption :"",
      alreadyAttemptedOptionApt :"",

    };

    this.timer = 0;
    this.startTimer = this.startTimer.bind(this);
    this.countDown = this.countDown.bind(this);




    firebase.auth().onAuthStateChanged(function(user) {
      if (user) {





        // User is signed in.
        var displayName = user.displayName;
        var email = user.email;
        var emailVerified = user.emailVerified;
        var photoURL = user.photoURL;
        var isAnonymous = user.isAnonymous;
        var uid = user.uid;
        var providerData = user.providerData;
       // console.log(user);
        // document.getElementById("signbutton").style.display = "none";
        // document.getElementById("profiletag").style.visibility = "visible";
        // document.getElementById("username").innerHTML = displayName;
        // document.getElementById("dp").src = user.photoURL;

        // ...
      } else {
        // User is signed out.
        // ...
        // document.getElementById("signbutton").style.visibility = "visible";
        // document.getElementById("profiletag").style.display = "none";
      }
    });
  }

//    startTimer(duration) {
//     var timer = duration, minutes, seconds;
//     setInterval(function () {
//         minutes = parseInt(timer / 60, 10);
//         seconds = parseInt(timer % 60, 10);

//         minutes = minutes < 10 ? "0" + minutes : minutes;
//         seconds = seconds < 10 ? "0" + seconds : seconds;

//         var dis = document.getElementById("timeleft").innerHTML;
//         console.log(dis);
//         dis.innerHTML = minutes + ":" + seconds;

//         if (--timer < 0) {
//             timer = duration;
//         }
//     }, 1000);

//     this.startTimer(duration);
// }





  check() {
    firebase.auth().onAuthStateChanged(function(user) {
      if (user) {
        // User is signed in.
        var displayName = user.displayName;
        var email = user.email;
        var emailVerified = user.emailVerified;
        var photoURL = user.photoURL;
        var isAnonymous = user.isAnonymous;
        var uid = user.uid;
        var providerData = user.providerData;
        UID = uid;
    //    console.log(user);

        // ...
      } else {
        // User is signed out.
        // ...
      }
    });
  }


   getTimeRemaining(endtime) {
    var t = Date.parse(endtime) - Date.parse(new Date());
    var seconds = Math.floor((t / 1000) % 60);
    var minutes = Math.floor((t / 1000 / 60) % 60);
    var hours = Math.floor((t / (1000 * 60 * 60)) % 24);
    var days = Math.floor(t / (1000 * 60 * 60 * 24));
    return {
      'total': t,
      'days': days,
      'hours': hours,
      'minutes': minutes,
      'seconds': seconds
    };
  }

   initializeClock(id, endtime) {
    var clock = document.getElementById(id);
    var daysSpan = clock.querySelector('.days');
    var hoursSpan = clock.querySelector('.hours');
    var minutesSpan = clock.querySelector('.minutes');
    var secondsSpan = clock.querySelector('.seconds');

    function updateClock() {
      var t = this.getTimeRemaining(endtime);

      daysSpan.innerHTML = t.days;
      hoursSpan.innerHTML = ('0' + t.hours).slice(-2);
      minutesSpan.innerHTML = ('0' + t.minutes).slice(-2);
      secondsSpan.innerHTML = ('0' + t.seconds).slice(-2);

      if (t.total <= 0) {
        clearInterval(timeinterval);
      }
    }

    updateClock();
    var timeinterval = setInterval(updateClock, 1000);
  }



  secondsToTime(secs){
    let hours = Math.floor(secs / (60 * 60));

    let divisor_for_minutes = secs % (60 * 60);
    let minutes = Math.floor(divisor_for_minutes / 60);

    let divisor_for_seconds = divisor_for_minutes % 60;
    let seconds = Math.ceil(divisor_for_seconds);

    let obj = {
      "h": hours,
      "m": minutes,
      "s": seconds
    };
    return obj;
  }

  startTimer() {
    if (this.timer == 0 && this.state.seconds > 0) {
      this.timer = setInterval(this.countDown, 1000);
    }
  }

  countDown() {
    // Remove one second, set state so a re-render happens.
    let seconds = this.state.seconds - 1;
    this.setState({
      time: this.secondsToTime(seconds),
      seconds: seconds,
    });

    // Check if we're at zero.
    if (seconds == 0) { 
      clearInterval(this.timer);

      this.submitProgrammingQuiz();
     // this.submitAptitude();
    }
  }


