import React, { Component } from 'react'
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import * as firebase from 'firebase';
import {withGetScreen} from 'react-getscreen'
import next from './images/next.png';
import back from './images/back.png';
import {Helmet} from 'react-helmet';



let length = 0;
let temp = [];
let selected;
let clickedOnce = false;
let uid;
export class App extends Component {


  constructor(props){
    super(props);
    this.state={
      currentScore : 0,
      correct:0,
      wrong:0,
      questions : [],
      currentIndex : 0,


    }

    firebase.auth().onAuthStateChanged(function(user) {
      if (user) {
        // User is signed in.
        var displayName = user.displayName;
        var email = user.email;
        var emailVerified = user.emailVerified;
        var photoURL = user.photoURL;
        var isAnonymous = user.isAnonymous;
        var uid = user.uid;
        var providerData = user.providerData;
        console.log(user);
        document.getElementById('signbutton').style.display="none";
        document.getElementById('profiletag').style.visibility="visible";
        document.getElementById('username').innerHTML=displayName;
        document.getElementById('dp').src=user.photoURL;


        // ...
      } else {
        // User is signed out.
        // ...
        document.getElementById('signbutton').style.visibility="visible";
        document.getElementById('profiletag').style.display="none";

      }
    });
  }


  check(){
    firebase.auth().onAuthStateChanged(function(user) {
      if (user) {
        // User is signed in.
        var displayName = user.displayName;
        var email = user.email;
        var emailVerified = user.emailVerified;
        var photoURL = user.photoURL;
        var isAnonymous = user.isAnonymous;
        var uid = user.uid;
        var providerData = user.providerData;
        console.log(user);

        // ...
      } else {
        // User is signed out.
        // ...
      }
    });
  }

  componentDidMount(){
    //this.check();
    const rootRef = firebase.database().ref();
    const speedRef = rootRef.child('quiz1');

    speedRef.on('value', snap=>{
      let questions  = snap.val();
      let newState = [];
      for(let x in questions){
        newState.push({
          id : x,
          op1 : questions[x].op1,
          op2 : questions[x].op2,
          op3 : questions[x].op3,
          op4 : questions[x].op4,
          q: questions[x].question,
          answer : questions[x].answer
        })
        length = length + 1;
        console.log(length)
      }

      length = newState.length;

     this.setState({
       questions : newState,

     }) 

    });



  }

  setAnswer(event) {
    selected = event.target.value;
    console.log(selected)
  }


  toggleStar(postRef, uid) {
    var amandaAgeRef = ref.child("leaderboard1").child().child('age');

    amandaAgeRef.transaction(function(currentAge) {
    return currentAge + 1;
});
  }

  submitAnswer(){
    document.getElementById('btn').style.visibility="hidden";
    clickedOnce = true;
    console.log(selected);
    let that  = this;
    let a = selected;
    console.log(this.state.currentIndex)
    let q = this.state.currentIndex;
    firebase.database().ref('/quiz1/' + 'q' + (q+1)).once('value').then(function(snap){
      console.log(snap.val().answer);
      if(a===snap.val().answer){
        let cc = that.state.currentScore + 1;
        let cr = that.state.correct + 1;



        that.setState({
          currentScore : cc,
          correct : cr
        });   

         //NEXT QUESTION 

         console.log("ho");
         let c = that.state.currentIndex + 1
         if(c < length){
          document.getElementById('btn').style.visibility="visible";

          that.setState({
            currentIndex : c
          });
          document.getElementById('o1').checked = false;
          document.getElementById('o2').checked = false;
          document.getElementById('o3').checked = false;
          document.getElementById('o4').checked = false;
         }

      }

      else {
        document.getElementById('div1').style.backgroundColor="#b71c1c";
        document.getElementById('div2').style.backgroundColor="#b71c1c";

        setInterval(function(){
          document.getElementById('div1').style.backgroundColor="#F06292";
          document.getElementById('div2').style.backgroundColor="#F06292";
        }, 500);


        let cr = that.state.wrong + 1;
        that.setState({
          wrong : cr
        });  


        //NEXT QUESTION 

        console.log("ho");
        let c = that.state.currentIndex + 1
        if(c < length){
         document.getElementById('btn').style.visibility="visible";

         that.setState({
           currentIndex : c
         });
         document.getElementById('o1').checked = false;
         document.getElementById('o2').checked = false;
         document.getElementById('o3').checked = false;
         document.getElementById('o4').checked = false;
        }

      }
    });
  }

  signin(){
    var provider = new firebase.auth.GoogleAuthProvider();

    firebase.auth().signInWithPopup(provider).then(function(result) {
      // This gives you a Google Access Token. You can use it to access the Google API.
      var token = result.credential.accessToken;
      // The signed-in user info.
      var user = result.user;
      console.log(user)
      // ...
    }).catch(function(error) {
      // Handle Errors here.
      var errorCode = error.code;
      var errorMessage = error.message;
      // The email of the user's account used.
      var email = error.email;
      // The firebase.auth.AuthCredential type that was used.
      var credential = error.credential;
      // ...
    });
  }

