import React, { Component } from 'react'
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import * as firebase from 'firebase';

let temp = [];

export class App extends Component {


  constructor(props){
    super(props);
    this.state={
      questions : [],

    }
  }

  componentDidMount(){

    const rootRef = firebase.database().ref();
    const speedRef = rootRef.child('quiz1');

    speedRef.on('value', snap=>{
      let questions  = snap.val();
      let newState = [];
      for(let x in questions){
        newState.push({
          id : x,
          op1 : questions[x].op1,
          op2 : questions[x].op2,
          op3 : questions[x].op3,
          op4 : questions[x].op4,
          q: questions[x].question

        })
      }

     this.setState({
       questions : newState
     }) 

    });

  }

  render() {

    return (
      <div className="App" style={{backgroundColor:"#F06292", height:"100%"}}>
        {this.state.questions.map(q=>{
          return(
            <div className="container-fluid">
            <div className="row">
              <div className="col-3">

              </div>

              <div className="col-6">
              <div style={{boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)", margin:"20px"}}>
                <div class="card-body">
                  <h5 class="card-title">{q.q}</h5>
                  <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                  <a href="#" class="btn btn-primary">Go somewhere</a>
                </div>
            </div>

              </div>

              <div className="col-3">

              </div>

            </div>


            </div>
          );
        })}
      </div>
    )
  }
}

export default App
