import React, { Component } from 'react'
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import * as firebase from 'firebase';

let temp = [];

export class App extends Component {


  constructor(props){
    super(props);
    this.state={
      questions : [],

    }
  }

  componentDidMount(){

    const rootRef = firebase.database().ref();
    const speedRef = rootRef.child('quiz1');

    speedRef.on('value', snap=>{
      let questions  = snap.val();
      let newState = [];
      for(let x in questions){
        newState.push({
          id : x,
          op1 : questions[x].op1,
          op2 : questions[x].op2,
          op3 : questions[x].op3,
          op4 : questions[x].op4,
          q: questions[x].question

        })
      }

     this.setState({
       questions : newState
     }) 

    });

  }

  setGender(event) {
    console.log(event.target.value);
  }

  render() {

    return (
      <div className="App" style={{backgroundColor:"#F06292", height:"100%"}}>
        {this.state.questions.map(q=>{
          return(
            <div className="container-fluid" style={{backgroundColor:"#F06292", height:"100%"}}>
            <div className="row">
              <div className="col-3">

              </div>

              <div className="col-6">
              <div style={{boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)", margin:"20px", backgroundColor:"white"}}>
                <div class="card-body">
                  <h1 class="card-title">{q.q}</h1>
                  <div onChange={this.setGender.bind(this)}>
                    <input type="radio" value={q.op1} class="form-radio" name="gender"/> {q.op1}
                    <input type="radio" value={q.op2} class="form-radio" name="gender"/> {q.op2}
                  </div>





                </div>

            </div>

              </div>

              <div className="col-3">

              </div>

            </div>


            </div>
          );
        })}
      </div>
    )
  }
}

export default App
