import React, { Component } from 'react'
import './App.css'
import * as firebase from 'firebase';

let temp = [];

export class App extends Component {


  constructor(props){
    super(props);
    this.state={
      questions : [],

    }
  }



  componentDidMount(){

    const rootRef = firebase.database().ref();
    const speedRef = rootRef.child('quiz1');

    speedRef.on('value', snap=>{
      let questions  = snap.val();
      let newState = [];
      for(let x in questions){
        newState.push({
          id : x,
          op1 : questions[x].op1,
          op2 : questions[x].op2
        })
      }

     this.setState({
       questions : newState
     }) 

    });

  }

  render() {

    return (
      <div className="App">
        {this.state.questions.map(q=>{
          return(
            <div>
            <h1>rhis is {q.op1}</h1>
            </div>
          );
        })}
      </div>
    )
  }
}

export default App
