import React, { Component } from 'react'
import './App.css'
import * as firebase from 'firebase';

let temp = [];

export class App extends Component {


  constructor(props){
    super(props);
    this.state={
      questions : [],

    }
  }



  componentDidMount(){

    const rootRef = firebase.database().ref();
    const speedRef = rootRef.child('quiz1');

    speedRef.on('value', snap=>{
     // console.log(snap.val())

      snap.forEach(function(val){
      //  console.log(val.val().op1);
        temp.push({
          question : val.val().question,
          ops : {
            op1 : val.val().op1,
            op2 : val.val().op2,
            op3 : val.val().op3,
            op4 : val.val().op4,
          }
        });
      })


    });

    console.log(temp)

    // this.setState({
    //   questions : temp,

    // });
    // console.log(questions) 

    this.setState({
      questions : [...temp]
    });

    console.log(this.state.questions)

  }

  render() {
    return (
      <div className="App">
        <h1>{this.state.speed}</h1>
      </div>
    )
  }
}

export default App
