import React, { Component } from 'react'
import './App.css'
import * as firebase from 'firebase';

export class App extends Component {


  constructor(){
    super();
    this.state={
      questions : [],
      options : [],
      speed : 10
    }
  }

  componentDidMount(){
    const rootRef = firebase.database().ref();
    const speedRef = rootRef.child('quiz1');
    speedRef.on('value', snap=>{
      console.log(snap.val())
      snap.forEach(function(val){
        console.log(val.val()['op1']);
        questions.push({
          questions : val.val()['op1']
        })
      })

      this.setState({
        speed:snap.val()
      })
    })

  }

  render() {
    return (
      <div className="App">
        <h1>{this.state.speed}</h1>
      </div>
    )
  }
}

export default App
